export default function CarHud() {
  const { isFuel, isMap, isSpeed, isSpeedType, isEngine, isSeatbelt, isLight, isCruise, isNitro, isGunAnim } = useContext(MainContext);
  return (
    <motion.div className='CarHud' animate={isMap ? {opacity: 1} : {opacity: 0}} style={isGunAnim ? {animation: `ShotScales .3s ease-out forwards`} : {animation: ``}}>
      {Fuel(isFuel)}
      <div className="FuelText"><p>{isFuel}L</p></div>
      {BackgroundNoos(isNitro)}
      <div className="SpeedText"><p>{isSpeed}</p></div>
      <div className="SpeedType"><p>{(isSpeedType === 'kmh') ? 'km/h' : 'mp/h'}</p></div>
      {SpeedoMeter(isSpeed)}


      {GetSVG('CruiseControl', isCruise)}

      {GetSVG('Engine', isEngine)}
      {GetSVG('Light', isLight)}
      {GetSVG('Belt', isSeatbelt)}
    </motion.div>
  )
}


